class TestSorting(unittest.TestCase):
  def test_empty(self):
    self.assertEqual(sorted([]), [])
    self.assertEqual(bubblesort([]), [])
    self.assertEqual(insertionsort([]), [])
    self.assertEqual(selectionsort([]), [])
    self.assertEqual(mergesort([]), [])
    self.assertEqual(quicksort([]), [])

  def test_long_floats(self):
    self.assertEqual(sorted(test_array.copy()), sorted_test_array)
    self.assertEqual(bubblesort(test_array.copy()), sorted_test_array)
    self.assertEqual(insertionsort(test_array.copy()), sorted_test_array)
    self.assertEqual(selectionsort(test_array.copy()), sorted_test_array)
    self.assertEqual(mergesort(test_array.copy()), sorted_test_array)
    self.assertEqual(quicksort(test_array.copy()), sorted_test_array)


